import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import {
  Play,
  Copy,
  Check,
  AlertCircle,
  Code,
  Globe,
  Zap,
  BookOpen
} from 'lucide-react';

const ApiTest = () => {
  const { user } = useAuth();
  const [apiDocs, setApiDocs] = useState(null);
  const [testResult, setTestResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [docsLoading, setDocsLoading] = useState(true);
  const [copied, setCopied] = useState(false);

  // Test form state
  const [testForm, setTestForm] = useState({
    method: 'GET',
    url: '/api/health',
    headers: {},
    body: {}
  });

  useEffect(() => {
    fetchApiDocs();
  }, []);

  const fetchApiDocs = async () => {
    try {
      setDocsLoading(true);
      const response = await api.get('/test/docs');
      setApiDocs(response.data.data);
    } catch (err) {
      console.error('Failed to fetch API docs:', err);
    } finally {
      setDocsLoading(false);
    }
  };

  const runApiTest = async () => {
    try {
      setLoading(true);
      setTestResult(null);

      const response = await api.post('/test/test', {
        method: testForm.method,
        url: testForm.url,
        headers: testForm.headers,
        body: testForm.body
      });

      setTestResult(response.data);
    } catch (err) {
      setTestResult({
        success: false,
        data: {
          error: {
            message: err.response?.data?.message || err.message,
            status: err.response?.status || 500
          }
        }
      });
    } finally {
      setLoading(false);
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const updateTestForm = (field, value) => {
    setTestForm(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const addHeader = () => {
    setTestForm(prev => ({
      ...prev,
      headers: {
        ...prev.headers,
        '': ''
      }
    }));
  };

  const updateHeader = (key, value, oldKey = null) => {
    setTestForm(prev => {
      const newHeaders = { ...prev.headers };
      if (oldKey && oldKey !== key) {
        delete newHeaders[oldKey];
      }
      if (value) {
        newHeaders[key] = value;
      } else {
        delete newHeaders[key];
      }
      return { ...prev, headers: newHeaders };
    });
  };

  if (docsLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading API documentation...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">API Testing & Documentation</h1>
          <p className="text-gray-600">Test and explore the Aphrodite Store API</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* API Tester */}
          <div className="bg-white rounded-lg shadow">
            <div className="p-6 border-b border-gray-200">
              <h2 className="text-xl font-semibold text-gray-900 flex items-center">
                <Zap className="h-5 w-5 mr-2" />
                API Tester
              </h2>
            </div>
            <div className="p-6 space-y-4">
              {/* Method and URL */}
              <div className="grid grid-cols-4 gap-2">
                <select
                  value={testForm.method}
                  onChange={(e) => updateTestForm('method', e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                >
                  <option value="GET">GET</option>
                  <option value="POST">POST</option>
                  <option value="PUT">PUT</option>
                  <option value="PATCH">PATCH</option>
                  <option value="DELETE">DELETE</option>
                </select>
                <input
                  type="text"
                  value={testForm.url}
                  onChange={(e) => updateTestForm('url', e.target.value)}
                  placeholder="/api/endpoint"
                  className="col-span-3 px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              {/* Headers */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Headers
                </label>
                <div className="space-y-2">
                  {Object.entries(testForm.headers).map(([key, value], index) => (
                    <div key={index} className="grid grid-cols-2 gap-2">
                      <input
                        type="text"
                        value={key}
                        onChange={(e) => updateHeader(e.target.value, value, key)}
                        placeholder="Header name"
                        className="px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      />
                      <input
                        type="text"
                        value={value}
                        onChange={(e) => updateHeader(key, e.target.value)}
                        placeholder="Header value"
                        className="px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      />
                    </div>
                  ))}
                  <button
                    onClick={addHeader}
                    className="text-sm text-blue-600 hover:text-blue-800"
                  >
                    + Add Header
                  </button>
                </div>
              </div>

              {/* Body */}
              {['POST', 'PUT', 'PATCH'].includes(testForm.method) && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Request Body (JSON)
                  </label>
                  <textarea
                    value={JSON.stringify(testForm.body, null, 2)}
                    onChange={(e) => {
                      try {
                        updateTestForm('body', JSON.parse(e.target.value));
                      } catch (err) {
                        // Invalid JSON, keep the text for editing
                      }
                    }}
                    rows={6}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 font-mono text-sm"
                    placeholder='{"key": "value"}'
                  />
                </div>
              )}

              {/* Run Test Button */}
              <button
                onClick={runApiTest}
                disabled={loading}
                className="w-full flex items-center justify-center px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? (
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                ) : (
                  <Play className="h-4 w-4 mr-2" />
                )}
                {loading ? 'Testing...' : 'Run Test'}
              </button>
            </div>
          </div>

          {/* API Documentation */}
          <div className="bg-white rounded-lg shadow">
            <div className="p-6 border-b border-gray-200">
              <h2 className="text-xl font-semibold text-gray-900 flex items-center">
                <BookOpen className="h-5 w-5 mr-2" />
                API Documentation
              </h2>
            </div>
            <div className="p-6 max-h-96 overflow-y-auto">
              {apiDocs && (
                <div className="space-y-4">
                  <div>
                    <h3 className="font-semibold text-gray-900 mb-2">Base URL</h3>
                    <div className="flex items-center space-x-2">
                      <code className="px-2 py-1 bg-gray-100 rounded text-sm">
                        {apiDocs.baseUrl}
                      </code>
                      <button
                        onClick={() => copyToClipboard(apiDocs.baseUrl)}
                        className="text-gray-400 hover:text-gray-600"
                      >
                        {copied ? <Check className="h-4 w-4" /> : <Copy className="h-4 w-4" />}
                      </button>
                    </div>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-900 mb-2">Quick Examples</h3>
                    <div className="space-y-2">
                      <div className="p-3 bg-gray-50 rounded">
                        <div className="flex items-center justify-between mb-1">
                          <span className="text-sm font-medium text-gray-700">Health Check</span>
                          <button
                            onClick={() => {
                              updateTestForm('method', 'GET');
                              updateTestForm('url', '/api/health');
                            }}
                            className="text-xs text-blue-600 hover:text-blue-800"
                          >
                            Use
                          </button>
                        </div>
                        <code className="text-xs text-gray-600">GET /api/health</code>
                      </div>

                      <div className="p-3 bg-gray-50 rounded">
                        <div className="flex items-center justify-between mb-1">
                          <span className="text-sm font-medium text-gray-700">Get Products</span>
                          <button
                            onClick={() => {
                              updateTestForm('method', 'GET');
                              updateTestForm('url', '/api/public/products');
                            }}
                            className="text-xs text-blue-600 hover:text-blue-800"
                          >
                            Use
                          </button>
                        </div>
                        <code className="text-xs text-gray-600">GET /api/public/products</code>
                      </div>

                      <div className="p-3 bg-gray-50 rounded">
                        <div className="flex items-center justify-between mb-1">
                          <span className="text-sm font-medium text-gray-700">Dashboard Stats</span>
                          <button
                            onClick={() => {
                              updateTestForm('method', 'GET');
                              updateTestForm('url', '/api/dashboard/stats');
                              updateTestForm('headers', { 'Authorization': 'Bearer your_token_here' });
                            }}
                            className="text-xs text-blue-600 hover:text-blue-800"
                          >
                            Use
                          </button>
                        </div>
                        <code className="text-xs text-gray-600">GET /api/dashboard/stats</code>
                      </div>
                    </div>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-900 mb-2">Available Endpoints</h3>
                    <div className="space-y-1 text-sm">
                      <div className="flex justify-between">
                        <span className="text-gray-600">Authentication:</span>
                        <span className="text-gray-900">{Object.keys(apiDocs.endpoints.auth).length} endpoints</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Products:</span>
                        <span className="text-gray-900">{Object.keys(apiDocs.endpoints.products).length} endpoints</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Categories:</span>
                        <span className="text-gray-900">{Object.keys(apiDocs.endpoints.categories).length} endpoints</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Dashboard:</span>
                        <span className="text-gray-900">{Object.keys(apiDocs.endpoints.dashboard).length} endpoints</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Orders:</span>
                        <span className="text-gray-900">{Object.keys(apiDocs.endpoints.orders).length} endpoints</span>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Test Results */}
        {testResult && (
          <div className="mt-8 bg-white rounded-lg shadow">
            <div className="p-6 border-b border-gray-200">
              <h2 className="text-xl font-semibold text-gray-900 flex items-center">
                <Code className="h-5 w-5 mr-2" />
                Test Results
              </h2>
            </div>
            <div className="p-6">
              <div className="mb-4">
                <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                  testResult.success 
                    ? 'bg-green-100 text-green-800' 
                    : 'bg-red-100 text-red-800'
                }`}>
                  {testResult.success ? (
                    <>
                      <Check className="h-4 w-4 mr-1" />
                      Success
                    </>
                  ) : (
                    <>
                      <AlertCircle className="h-4 w-4 mr-1" />
                      Error
                    </>
                  )}
                </div>
              </div>

              <div className="space-y-4">
                {/* Request */}
                <div>
                  <h3 className="font-medium text-gray-900 mb-2">Request</h3>
                  <div className="bg-gray-50 rounded p-4">
                    <div className="mb-2">
                      <span className="font-mono text-sm">
                        {testResult.data?.request?.method} {testResult.data?.request?.url}
                      </span>
                    </div>
                    {testResult.data?.request?.headers && (
                      <div className="mb-2">
                        <span className="text-sm font-medium text-gray-700">Headers:</span>
                        <pre className="text-xs text-gray-600 mt-1">
                          {JSON.stringify(testResult.data.request.headers, null, 2)}
                        </pre>
                      </div>
                    )}
                    {testResult.data?.request?.body && (
                      <div>
                        <span className="text-sm font-medium text-gray-700">Body:</span>
                        <pre className="text-xs text-gray-600 mt-1">
                          {JSON.stringify(testResult.data.request.body, null, 2)}
                        </pre>
                      </div>
                    )}
                  </div>
                </div>

                {/* Response */}
                <div>
                  <h3 className="font-medium text-gray-900 mb-2">Response</h3>
                  <div className="bg-gray-50 rounded p-4">
                    {testResult.data?.response ? (
                      <>
                        <div className="mb-2">
                          <span className="font-mono text-sm">
                            {testResult.data.response.status} {testResult.data.response.statusText}
                          </span>
                        </div>
                        <pre className="text-xs text-gray-600 overflow-x-auto">
                          {JSON.stringify(testResult.data.response.data, null, 2)}
                        </pre>
                      </>
                    ) : (
                      <div className="text-red-600">
                        <pre className="text-xs">
                          {JSON.stringify(testResult.data?.error, null, 2)}
                        </pre>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ApiTest;
