import React, { useState, useEffect } from 'react';
import toast from 'react-hot-toast';
import api from '../services/api';
import './HeroManager.css'; // Reuse hero manager styles

function CollectionManager() {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [collectionData, setCollectionData] = useState({
    title: 'Our Collections',
    subtitle: 'Explore our curated selection of premium products',
    imageUrl: null
  });
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);

  useEffect(() => {
    fetchCollectionSettings();
  }, []);

  const fetchCollectionSettings = async () => {
    try {
      setLoading(true);
      const response = await api.get('/settings/collection');
      setCollectionData(response.data);

      // Set preview for existing image
      if (response.data.imageUrl) {
        const imageUrl = response.data.imageUrl.startsWith('http')
          ? response.data.imageUrl
          : `${process.env.REACT_APP_BACKEND_URL || 'http://localhost:3001'}${response.data.imageUrl}`;
        setImagePreview(imageUrl);
      }
    } catch (error) {
      console.error('Error fetching collection settings:', error);
      toast.error('Failed to load collection settings');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCollectionData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);

      // Create preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleRemoveImage = () => {
    setImageFile(null);
    setImagePreview(null);
    setCollectionData(prev => ({
      ...prev,
      imageUrl: null
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setSaving(true);
      const formData = new FormData();

      // Append text fields
      formData.append('title', collectionData.title);
      formData.append('subtitle', collectionData.subtitle);

      // Append image if new file selected
      if (imageFile) {
        formData.append('collection', imageFile);
      } else if (collectionData.imageUrl === null) {
        formData.append('imageUrl', 'null');
      }

      await api.post('/settings/collection', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      toast.success('Collection settings updated successfully!');
      fetchCollectionSettings();
      setImageFile(null);
    } catch (error) {
      console.error('Error updating collection settings:', error);
      toast.error(error.response?.data?.error || 'Failed to update collection settings');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="hero-manager">
        <div className="hero-manager-header">
          <h1>Collection Section Manager</h1>
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="hero-manager">
      <div className="hero-manager-header">
        <h1>Collection Section Manager</h1>
        <p>Manage the "Our Collections" section header image and text</p>
      </div>

      <form onSubmit={handleSubmit} className="hero-form">
        <div className="form-section">
          <h2>Header Image</h2>
          <p className="form-help">Upload a banner image for the collections section (optional)</p>

          <div className="image-upload-container">
            {imagePreview ? (
              <div className="image-preview">
                <img src={imagePreview} alt="Collection preview" />
                <button
                  type="button"
                  className="remove-image-btn"
                  onClick={handleRemoveImage}
                >
                  <i className="bx bx-x"></i> Remove Image
                </button>
              </div>
            ) : (
              <div className="image-upload-placeholder">
                <i className="bx bx-image-add"></i>
                <p>No image uploaded</p>
              </div>
            )}

            <input
              type="file"
              id="collectionImage"
              accept="image/*"
              onChange={handleImageChange}
              style={{ display: 'none' }}
            />

            <label htmlFor="collectionImage" className="upload-btn">
              <i className="bx bx-upload"></i> Choose Image
            </label>
          </div>
        </div>

        <div className="form-section">
          <h2>Text Content</h2>

          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              type="text"
              id="title"
              name="title"
              value={collectionData.title}
              onChange={handleInputChange}
              placeholder="Our Collections"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="subtitle">Subtitle</label>
            <input
              type="text"
              id="subtitle"
              name="subtitle"
              value={collectionData.subtitle}
              onChange={handleInputChange}
              placeholder="Explore our curated selection of premium products"
              required
            />
          </div>
        </div>

        <div className="form-actions">
          <button
            type="button"
            className="btn-secondary"
            onClick={fetchCollectionSettings}
            disabled={saving}
          >
            <i className="bx bx-reset"></i> Reset
          </button>

          <button
            type="submit"
            className="btn-primary"
            disabled={saving}
          >
            {saving ? (
              <>
                <i className="bx bx-loader-alt bx-spin"></i> Saving...
              </>
            ) : (
              <>
                <i className="bx bx-save"></i> Save Changes
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );
}

export default CollectionManager;
